mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.acsHist.repository.AcsHistMapper.getAcsHistList */ select seq --접속이력아이디 , acs_ip --접속아이피 , req_url --접속요청주소 , req_method --접속요청메소드 , reg_id --접속자아이디 , reg_dt --접속요청시간 ,vum.member_nm as memberNm --접속자명 from com_acs_hist ca left join view_user_member vum on vum.member_id = ca.reg_id where 1=1
and vum.member_nm like concat('%', #{memberNm}, '%'}
and req_url like concat('%', #{reqUrl}, '%')
-- stDt 날짜 형식 yyyy-mm-dd and reg_dt >= concat(#{stDt}, '00:00:00')
and reg_dt <= concat(#{edDt}, '23:59:59')

mapper.insert:
        insert into kgst.com_acs_hist (
			  acs_ip
			, req_url
			, req_method
			, reg_id
			, reg_dt
		)
		values (
			  #{acsIp}
			, #{reqUrl}
			, #{reqMethod}
			, #{regId}
			, now()
		)
