mapper.insert:
insert into kgst.ebs_base_code (
      code_id
    , code_kind
    , code_nm
    , bg_Dt
    , ed_dt
    , ord
    , curr_yn
    , rmk
    , use_yn
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values (
      #{codeId}
    , #{codeKind}
    , #{codeNm}
    , #{bgDt}
    , #{edDt}
    , #{ord}
    , #{currYn}
    , #{rmk}
    , #{useYn}
    , #{regId}
    , now()
    , NULL
    , NULL
)

mapper.update:
update ebs_base_code
set
      code_kind = #{codeKind}
    , code_nm = #{codeNm}
    , bg_dt = #{bgDt}
    , ed_dt = #{edDt}
    , rmk = #{rmk}
    , ord = #{ord}
    , use_yn = #{useYn}
    , curr_yn = #{currYn}
    , mod_id = #{modId}
    , mod_dt = #{modDt}
where seq = #{seq}
