mapper.update:
        update com_board
        set
	          brd_sj = #{brdSj}
	        , brd_cn = #{brdCn}
	        , pup_yn = #{pupYn}
	        , pup_bg_dt = #{pupBgDt}
	        , pup_ed_dt = #{pupEdDt}
	        , ord = #{ord}
	        , file_group_id = #{fileGroupId}
	        , mod_id = #{modId}
	        , mod_dt = #{modDt}
        WHERE brd_id = #{brdId}

update com_board
set noti_inq_cnt = #{notiInqCnt}
where brd_id = #{brdId}

mapper.insert:
        insert into	kgst.com_board (
			  brd_type
			, brd_sj
			, brd_cn
			, noti_inq_cnt
			, pup_yn
			, pup_bg_dt
			 , pup_ed_dt        
			, ord
			, file_group_id
			, reg_id
			, reg_dt
		)
		values (
			  #{brdType}
			, #{brdSj}
			, #{brdCn}
			, #{notiInqCnt}
			, #{pupYn}
			, #{pupBgDt}
			 , #{pupEdDt}        
			, #{ord}
			, #{fileGroupId}
			, #{regId}
			, now()
		)
