mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.bbs.repository.BoardMapper.getBoardList */ select row_number() over(order by cb.reg_dt desc) as num , cb.brd_id , cb.brd_type --보드타입 , cb.brd_sj , cb.brd_cn , cb.noti_inq_cnt , cb.pup_yn , cb.pup_bg_dt , cb.pup_ed_dt , cb.ord , cb.file_group_id , cb.reg_id , cb.reg_dt , cb.mod_id , cb.mod_dt , kgst.fngetlangnm(view.user_nm_kg, view.user_nm_ru, view.user_nm_kg, #{lang}) as reg_nm , cb.important_yn , cb.hidden_yn , kgst.fngetlangnm(cd.dept_nm1, cd.dept_nm2, cd.dept_nm3, #{lang}) as dept_nm from com_board cb left join kgst.view_user_member view on view.user_id = cb.reg_id left join com_dept cd on cd.dept_cd = cb.cmt_cd where cb.brd_type = #{brdType}
and cb.brd_sj like concat('%', #{brdSj}, '%')
and kgst.fngetlangnm(view.user_nm_kg, view.user_nm_ru, view.user_nm_kg, #{lang}) like concat('%', #{regNm}, '%')
and kgst.fngetlangnm(cd.dept_nm1, cd.dept_nm2, cd.dept_nm3, #{lang}) like concat('%', #{deptNm}, '%')
order by case when cb.important_yn = 'Y' then 1 else 3 end, cb.reg_dt desc

mapper.update:
        update com_board
        set
	          brd_sj = #{brdSj}
	        , brd_cn = #{brdCn}
	        , pup_yn = #{pupYn}
	        , pup_bg_dt = #{pupBgDt}
	        , pup_ed_dt = #{pupEdDt}
	        , ord = #{ord}
	        , file_group_id = #{fileGroupId}
	        , mod_id = #{modId}
	        , mod_dt = #{modDt}
			, important_yn = #{importantYn}
			, hidden_yn = #{hiddenYn}
        WHERE brd_id = #{brdId}

update com_board
set noti_inq_cnt = #{notiInqCnt}
where brd_id = #{brdId}

mapper.insert:
        insert into	kgst.com_board (
			  brd_type
			, brd_sj
			, brd_cn
			, noti_inq_cnt
			, pup_yn
			, pup_bg_dt
			 , pup_ed_dt        
			, ord
			, file_group_id
			, reg_id
			, reg_dt
			, important_yn
			, hidden_yn
			, cmt_cd
		)
		values (
			  #{brdType}
			, #{brdSj}
			, #{brdCn}
			, #{notiInqCnt}
			, #{pupYn}
			, #{pupBgDt}
			 , #{pupEdDt}        
			, #{ord}
			, #{fileGroupId}
			, #{regId}
			, now()
			, #{importantYn}
			, #{hiddenYn}
			, #{cmtCd}
		)
