mapper.insert:
insert into com_ccof (
      user_id
    , dept_cd
    , ord
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values (
      #{userId}
    , #{deptCd}
    , #{ord}
    , #{regId}
    , now()
    , null
    , null
)

mapper.update:
update com_ccof
set
      dept_cd = #{deptCd}
    , mod_id = #{modId}
    , mod_dt = #{modDt}
