mapper.insert:

    select coalesce(max(lng_id) + 1, 10000) from com_lng_code

insert into com_lng_code (
      lng_id
    , lng_ty1
    , lng_ty2
    , lng_ty3
    , rmk
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values (
      #{lngId}
    , #{lngTy1}
    , #{lngTy2}
    , #{lngTy3}
    , #{rmk}
    , #{regId}
    , now()
    , null
    , null
)

mapper.update:
update com_lng_code
set
      lng_ty1 = #{lngTy1}
    , lng_ty2 = #{lngTy2}
    , lng_ty3 = #{lngTy3}
    , rmk = #{rmk}
    , mod_id = #{modId}
    , mod_dt = #{modDt}
where lng_id = #{lngId}
