mapper.insert:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.member.repository.MemberMapper.insertMember */
insert into com_member (
      member_id
    , member_nm_kg
    , member_nm_ru
    , age_cd
    , cmit_cd
    , dept_id
    , poly_cd
    , poly_nm
    , rsdn_rgst_nmbr
    , email
    , gen_cd
    , profile_img_path
    , mbl_no
    , msg_rcv_yn
    , doc_mgr_yn
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values (
      #{memberId}
    , #{memberNmKg}
    , #{memberNmRu}
    , #{ageCd}
    , #{cmitCd}
    , #{deptId}
    , #{polyCd}
    , #{polyNm}
    , #{rsdnRgstNmbr}
    , #{email}
    , #{genCd}
    , #{profileImgPath}
    , #{mblNo}
    , #{msgRcvYn}
    , #{docMgrYn}
    , #{regId}
    , now()
    , null
    , null
)

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.member.repository.MemberMapper.updateMember */
update com_member
set
      member_nm_kg = #{memberNmKg}
    , member_nm_ru = #{memberNmRu}
    , age_cd = #{ageCd}
    , cmit_cd = #{cmitCd}
    , dept_id = #{deptId}
    , poly_cd = #{polyCd}
    , poly_nm = #{polyNm}
    , rsdn_rgst_nmbr = #{rsdnRgstNmbr}
    , email = #{email}
    , gen_cd = #{genCd}
    , profile_img_path = #{profileImgPath}
    , mbl_no = #{mblNo}
    , msg_rcv_yn = #{msgRcvYn}
    , doc_mgr_yn = #{docMgrYn}
    , mod_id = #{modId}
    , mod_dt = now()
where member_id = #{memberId}

mapper.delete:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.member.repository.MemberMapper.deleteMember */
delete from com_member
where member_id = #{memberId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.member.repository.MemberMapper.selectListMemberByPoly */ select member_id -- 국회의원아이디 , member_nm_kg -- 이름(키르키즈어) , member_nm_ru -- 이름(러시아어) , poly_cd -- 정당코드 , poly_nm -- 정당이름 from com_member where 1=1
and poly_cd = #{polyCd}
order by member_nm_kg
