mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.myPage.message.repository.MsgMapper.selectListRcv */ select em.msg_id --메세지아이디 , em.cnc_dt --취소일시 , em.msg_cn --메세지내용 , em.msg_sj --메세지제목 , em.rcv_dt --수신일시 , em.rcv_id --수신자아이디 , em.send_id --발신자아이디 , em.file_group_id --파일그룹아이디 , em.msg_div --발송구분 발송(S) 수신(R) , em.msg_group_id --메세지그룹아이디 , em.reg_id , em.reg_dt , em.mod_id , em.mod_dt , em.send_dt , kgst.fngetlangnm(vum.user_nm_kg, vum.user_nm_ru, vum.user_nm_kg, #{lang}) as send_nm from ebs_msg em inner join view_user_member vum on vum.user_id = em.send_id where em.msg_div = 'R' and em.rcv_id = #{rcvId}
and em.msg_sj like concat('%', #{msgSj}, '%')
and vum.user_nm_ru like concat('%', #{sendNm}, '%')
and vum.user_nm_kg like concat('%', #{sendNm}, '%')
order by em.send_dt desc

/* QueryID : kr.co.bestiansoft.ebillservicekg.myPage.message.repository.MsgMapper.selectListSend */ select em.msg_id --메세지아이디 , em.cnc_dt --취소일시 , em.msg_cn --메세지내용 , em.msg_sj --메세지제목 , em.rcv_dt --수신일시 , em.rcv_id --수신자아이디 , em.send_id --발신자아이디 , em.file_group_id --파일그룹아이디 , em.msg_div --발송구분 발송(S) 수신(R) , em.msg_group_id --메세지그룹아이디 , em.reg_id , em.reg_dt , em.mod_id , em.mod_dt , em.send_dt , kgst.fngetlangnm(vum.user_nm_kg, vum.user_nm_ru, vum.user_nm_kg, #{lang}) as rcv_nm from ebs_msg em left join view_user_member vum on vum.user_id = em.rcv_id where msg_div = 'S' and send_id = #{sendId}
and msg_sj like concat('%', #{msgSj}, '%')
and vum.user_nm_ru like concat('%', #{rcvNm}, '%')
and vum.user_nm_kg like concat('%', #{rcvNm}, '%')
order by send_dt desc

mapper.insert:
/* QueryID : kr.co.bestiansoft.ebillservicekg.myPage.message.repository.MsgMapper.insertMsg */
insert into ebs_msg (
      msg_cn
    , msg_sj
    , rcv_dt
    , rcv_id
    , send_dt
    , send_id
    , file_group_id
    , msg_div
    , msg_group_id
)
values (
      #{msgCn}
    , #{msgSj}
    , null
    , #{rcvId}
    , now()
    , #{sendId}
    , #{fileGroupId}
    , #{msgDiv}
    , #{msgGroupId}
)
