mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.user.repository.UserMapper.selectListUser */ select cu.dept_cd --부서코드 , cu.email --이메일 , cu.gen_cd --성별(M,F) , cu.profile_img_path --프로필이미지경로 , cu.job_cd --직급코드 , cu.msg_rcv_yn --메세지수신여부 , cu.pos_cd , cu.user_id --직원아이디 , kgst.fngetlangnm(cu.user_nm_kg,cu.user_nm_ru,cu.user_nm_kg,#{lang}) as user_nm , cu.user_nm_kg --이름(키르키즈어) , cu.user_nm_ru --이름(러시아어) , cu.dept_head_yn --부서장여부 , cu.doc_mgr_yn --문서관리자여부 , cu.reg_id , cu.reg_dt , cu.mod_id , cu.mod_dt , kgst.fngetlangnm(cd.dept_nm1,cd.dept_nm2,cd.dept_nm1,#{lang}) as dept_nm , kgst.fngetlangnm(cc.code_nm1,cc.code_nm2,cc.code_nm1,#{lang}) as job_nm from com_user cu left join com_dept cd on cd.dept_cd = cu.dept_cd left join com_code_detail cc on cc.code_id = cu.job_cd where 1=1
and cu.user_nm_ru like concat('%', #{userNm}, '%')
and cu.user_nm_kg like concat('%', #{userNm}, '%')
and cu.dept_cd = #{deptCd}
order by reg_dt desc

mapper.insert:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.user.repository.UserMapper.insertUser */
insert into com_user (
      dept_cd --부서코드
    , email --이메일
    , gen_cd --성별(M,F)
    , profile_img_path --프로필이미지경로
    , job_Cd --직급코드
    , msg_rcv_yn --메세지수신여부
    , pos_cd
    , user_id --직원아이디
    , user_nm_kg --이름(키르키즈어)
    , user_nm_ru --이름(러시아어)
    , dept_head_yn --부서장여부
    , doc_mgr_yn --문서관리자여부
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values (
      #{deptCd}
    , #{email}
    , #{genCd}
    , #{profileImgPath}
    , #{jobCd}
    , #{msgRcvYn}
    , #{posCd}
    , #{userId}
    , #{userNmKg}
    , #{userNmRu}
    , #{deptHeadYn}
    , #{docMgrYn}
    , #{regId}
    , now()
    , null
    , null
)

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.user.repository.UserMapper.updateUser */
update com_user
set
      dept_cd = #{deptCd}
    , email = #{email}
    , gen_cd = #{genCd}
    , profile_img_path = #{profileImgPath}
    , job_Cd = #{jobCd}
    , msg_rcv_yn = #{msgRcvYn}
    , pos_cd = #{posCd}
    , user_id = #{userId}
    , user_nm_kg = #{userNmKg}
    , user_nm_ru = #{userNmRu}
    , dept_head_yn = #{deptHeadYn}
    , doc_mgr_yn = #{docMgrYn}
    , mod_id = #{modId}
    , mod_dt = now()
where user_id = #{userId}

mapper.delete:
/* QueryID : kr.co.bestiansoft.ebillservicekg.admin.user.repository.UserMapper.deleteUser */
delete from com_user
where user_id = #{userId}

mapper.update:
update com_member
set
      email = #{email}
    , msg_rcv_yn = #{msgRcvYn}
    , profile_img_path = #{profileImgPath}
    , mbl_no = #{mblNo}
