mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.agree.repository.AgreeMapper.getAgreeList */ select em.bill_id --안건아이디 , em.bill_name_kg --안건이름kg , em.bill_name_ru --안건이름ru , em.ppsr_nm --제안자 , em.stat_cd --상태 , case when (ep.sign_dt is not null and ep.sign_cnc_dt is null) then to_char(ep.sign_dt, 'DD-MM-YYYY') else null end as signDate --동의날짜 , scl_dsc_rcp_nmb --사회토론번호 from ebs_proposer ep left join ebs_master em on em.bill_id = ep.bill_id where ep.proposer_id = #{userId} and em.na_term_cd = '14' and em.stat_cd in ('ST010', 'ST020', 'ST030', 'ST040')
and (em.bill_name_ru like concat('%', #{billName}, '%') or em.bill_name_kg like concat('%', #{billName}, '%'))
and em.stat_cd = #{statCd}
and em.ppsl_dt >= #{ppslDt}

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.setBillAgree */ update ebs_proposer set mod_id = #{modId} -- 수정자 아이디 , mod_dt = now() -- 수정날짜 -- 서명 취소 , sign_cnc_dt = now()
-- 서명 동의 , sign_dt = now() , sign_cnc_dt = null
where bill_id = #{billId} and proposer_id = #{userId}
