mapper.insert:
        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.insertApplyBill */

       insert into kgst.ebs_master (
			  bill_id 		-- 안건아이디
			, scl_dsc_rcp_nmb	-- 사회토론번호
			, na_term_cd	-- 대수
			, ppsl_knd_cd	-- 제안자 종류
			, bill_name_kg	-- 안건이름kg
			, bill_name_ru  -- 안건이름ru
			, ppsr_id		-- 제안자 아이디
			, ppsl_dt		-- 제안일자
			 , etc_kg  -- 비고kg
			 , etc_ru  -- 비고ru
			, reg_id		-- 등록자
			, reg_dt		-- 등록 날짜
			 , bill_no  -- 안건번호
			 , ppsl_dt  -- 제안날짜
		)
		values (
			  #{billId}
			, #{sclDscRcpNmb}
			, #{naTermCd}
			, #{ppslKndCd}
			, #{billNameKg}
			, #{billNameRu}
			, #{ppsrId}
			, to_char(now(), 'YYYYMMDD')
			, #{etcKg} 
			, #{etcRu} 
			, #{ppsrId}
			, now()
			, #{billNo} 
			, #{ppslDt} 
		)

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.selectListApply */ select AA.bill_id , kgst.fngetlangnm(AA.bill_name_kg,AA.bill_name_ru,AA.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as member_nm -- 제안자 이름 , AA.scl_dsc_rcp_nmb , AA.ppsl_dt , AA.reg_dt , AA.stat_nm , AA.stat_cd , AA.na_term_cd from ( select pl.bill_id , max(pl.bill_name_kg) as bill_name_kg , max(pl.bp_instance_id) as bp_instance_id , max(pl.bill_name_ru) as bill_name_ru , max(pl.na_term_cd) as na_term_cd , max(pl.current_step_id) as current_step_id , max(pl.ppsr_id) as ppsr_id , max(pl.ppsl_dt) as ppsl_dt , max(pl.scl_dsc_rcp_nmb) as scl_dsc_rcp_nmb , max(pl.reg_dt) as reg_dt , max(ebs.stat_cd) as stat_cd , kgst.fngetcodenm('1010',max(ebs.stat_cd),#{lang}) as stat_nm from kgst.view_bill_process_list pl join kgst.ebs_bp_step ebs on (pl.bp_df_id = ebs.bp_df_id and pl.current_step_id = ebs.step_id) group by bill_id ) AA join kgst.view_user_member vum on (AA.ppsr_id = vum.user_id) where 1=1 and AA.ppsr_id = #{loginId}
and AA.na_term_cd = #{naTermCd}
and AA.bill_name_kg like concat('%', #{billName}, '%')
and AA.bill_name_ru like concat('%', #{billName}, '%')
and AA.scl_dsc_rcp_nmb like concat('%', #{sclDscRcpNmb}, '%')
and AA.stat_cd = #{statCd}
and AA.ppsl_dt = #{ppslDt}
order by AA.reg_dt desc

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateApplyByBillId */
        update kgst.ebs_master
		set
		  bill_name_kg = #{billNameKg}	-- 안건이름kg
		, bill_name_ru = #{billNameRu}  -- 안건이름ru
		, etc_kg = #{etcKg} 			-- 비고kg
		, etc_ru  = #{etcRu} 			-- 비고ru
		, bill_no = #{billNo} 
		, ppsl_dt = #{ppslDt} 
		, mod_id = #{loginId}			-- 등록자
		, mod_dt = now()				-- 등록 날짜
		WHERE bill_id = #{billId}

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.deleteApplyByBillId */
	delete from kgst.ebs_master
    where bill_id = #{billId}

mapper.insert:
        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.insertProposerList */

       insert into kgst.ebs_proposer (
			  bill_id 		-- 안건아이디
			, proposer_id	-- 제안자 아이디
			, ord			-- 순서
			, poly_cd		-- 정당 코드
			, poly_nm		-- 정당 이름
			, reg_id		-- 등록자
			, reg_dt		-- 등록 날짜
			 , sign_dt  -- 발의자 서명
		)
		values (
			  #{billId}
			, #{ppsrId}
			, #{ord}
			, #{polyCd}
			, #{polyNm}
			, #{ppsrId}
			, now()
			 , now() 
		)

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.deleteProposerByBillId */
	delete from kgst.ebs_proposer
    where bill_id = #{billId}

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateApplyBill */
        update kgst.ebs_master
		set
		  stat_cd = #{statCd}		-- 상태 코드
		WHERE bill_id = #{billId}

    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateBillPpsrRevoke */
        update kgst.ebs_proposer
		set
		  wt_cn = now()
		WHERE bill_id = #{billId}
			and proposer_id = #{ppsrId}

    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateBillStatus */
        update kgst.ebs_master
		set
		  stat_cd = #{statCd}		-- 상태 코드
		   , wt_cn = #{wtCn}  -- 철회 사유
		WHERE bill_id = #{billId}

mapper.select:
        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.getProposerList */
        select
			  proposer_id -- 제안자 아이디
		from kgst.ebs_proposer ep
		where ep.bill_id = #{billId}

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.deleteProposerByBillId */
	delete from ebs_proposer
    where proposer_id = #{ppsrId}

mapper.insert:
        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.insertBillProcess */

       insert into kgst.ebs_process (
			  bill_id 		-- 안건아이디
			, proc_id		-- 프로세스 아이디
			, ord			-- 순서
			, poly_cd		-- 정당 코드
			, poly_nm		-- 정당 이름
			, reg_id		-- 등록자
			, reg_dt		-- 등록 날짜
			 , sign_dt  -- 발의자 서명
		)
		values (
			  #{billId}
			, #{ppsrId}
			, #{ord}
			, #{polyCd}
			, #{polyNm}
			, #{regId}
			, now()
			 , now() 
		)

        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.insertHomeLaws */

       insert into kgst.home_laws (
			  title_kg 		-- 안건명 키르
			, title_ru		-- 안건명 러시아
		)
		values (
			  #{billNameKg}
			, #{billNameRu}
		)

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateFileDelete */
        update kgst.ebs_file
		set
		    mod_id = #{userId}
		  , mod_dt = now()
		  , delete_yn = 'Y'
		WHERE org_file_id = #{ebsFileVo.orgFileId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.selectBillAll */ select AA.bill_id , kgst.fngetlangnm(AA.bill_name_kg,AA.bill_name_ru,AA.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as member_nm -- 제안자 이름 , AA.scl_dsc_rcp_nmb , AA.reg_dt as ppslDt , AA.stat_nm , AA.stat_cd , AA.na_term_cd , AA.bill_no from ( select pl.bill_id , max(pl.bill_no) as bill_no , max(pl.bill_name_kg) as bill_name_kg , max(pl.bp_instance_id) as bp_instance_id , max(pl.bill_name_ru) as bill_name_ru , max(pl.na_term_cd) as na_term_cd , max(pl.current_step_id) as current_step_id , max(pl.ppsr_id) as ppsr_id , max(pl.ppsl_dt) as ppsl_dt , max(pl.scl_dsc_rcp_nmb) as scl_dsc_rcp_nmb , max(pl.reg_dt) as reg_dt , max(ebs.stat_cd) as stat_cd , kgst.fngetcodenm('1010',max(ebs.stat_cd),#{lang}) as stat_nm from kgst.view_bill_process_list pl join kgst.ebs_bp_step ebs on (pl.bp_df_id = ebs.bp_df_id and pl.current_step_id = ebs.step_id) group by bill_id ) AA join kgst.view_user_member vum on (AA.ppsr_id = vum.user_id) where 1=1
and AA.na_term_cd = #{naTermCd}
and AA.bill_name_kg like concat('%', #{billName}, '%')
and AA.bill_name_ru like concat('%', #{billName}, '%')
and AA.scl_dsc_rcp_nmb like concat('%', #{sclDscRcpNmb}, '%')
and AA.stat_cd = #{statCd}
order by AA.reg_dt desc

mapper.insert:
        /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.insertApplyCmt */

       insert into kgst.ebs_process_cmt (
			  bill_id 		-- 안건아이디
			, cmt_cd		-- 위원회코드
			, cmt_se_cd		-- 위원회구분코드(M:소관위,R:관련위)
			, rgtr_id		-- 등록자 아이디
			, reg_dt		-- 등록 날짜
		)
		values (
			  #{billId}
			, #{cmtCd}
			, 'M'
			, #{polyCd}
			, #{polyNm}
			, 'admin'		-- todo:: 아이디 정의 필요!!
			, now()
			 , now() 
		)

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateApplyCmt */
        update kgst.ebs_process_cmt
		set
		  cmt_cd = #{cmtCd}
		  , mdfr_id = 'admin'
		  , mdfcn_dt = now()
		WHERE bill_id = #{billId}
			and cmt_se_cd = 'M'
