			, #{billNameRu}
		)

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateFileDelete */
        update kgst.ebs_file
		set
		    mod_id = #{userId}
		  , mod_dt = now()
		  , delete_yn = 'Y'
		WHERE org_file_id = #{ebsFileVo.orgFileId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.selectBillAll */ select AA.bill_id , kgst.fngetlangnm(AA.bill_name_kg,AA.bill_name_ru,AA.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as member_nm -- 제안자 이름 , AA.scl_dsc_rcp_nmb , AA.reg_dt as ppslDt , AA.stat_nm , AA.stat_cd , AA.na_term_cd , AA.bill_no from ( select pl.bill_id , max(pl.bill_no) as bill_no , max(pl.bill_name_kg) as bill_name_kg , max(pl.bill_name_ru) as bill_name_ru , max(pl.na_term_cd) as na_term_cd , max(pl.current_step_id) as current_step_id , max(pl.ppsr_id) as ppsr_id , max(pl.ppsl_dt) as ppsl_dt , max(pl.scl_dsc_rcp_nmb) as scl_dsc_rcp_nmb , max(pl.reg_dt) as reg_dt , max(ebs.stat_cd) as stat_cd , kgst.fngetcodenm('1010',max(ebs.stat_cd),#{lang}) as stat_nm from kgst.view_bill_process_list pl join kgst.ebs_bp_step ebs on (pl.bp_df_id = ebs.bp_df_id and pl.current_step_id = ebs.step_id) group by bill_id ) AA join kgst.view_user_member vum on (AA.ppsr_id = vum.user_id) where 1=1
and AA.na_term_cd = #{naTermCd}
and AA.bill_name_kg like concat('%', #{billName}, '%')
and AA.bill_name_ru like concat('%', #{billName}, '%')
and AA.scl_dsc_rcp_nmb like concat('%', #{sclDscRcpNmb}, '%')
and AA.stat_cd = #{statCd}
order by AA.reg_dt desc

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.apply.repository.ApplyMapper.updateBillHome */
        update kgst.ebs_master
