mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.getRevokeList */ select em.bill_id --안건아이디 , em.scl_dsc_rcp_nmb --사회토론번호 , em.bill_name_kg --안건이름kg , em.bill_name_ru --안건이름ru , em.ppsr_nm --제안자 , to_char(em.reg_dt, 'DD-MM-YYYY') as regDate , em.stat_cd from ebs_master em where em.stat_cd in ('ST030','ST031') and em.ppsr_id = #{userId} and em.na_term_cd = '14'
and (em.bill_name_ru like concat('%', #{billName}, '%') or em.bill_name_kg like concat('%', #{billName}, '%'))
and em.stat_cd = #{statCd}
and em.ppsl_dt >= #{ppslDt}

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.updateRevokeRequset */
		update ebs_master 
		set 
			mod_id = #{modId} 			-- 수정자 아이디
			, mod_dt = now()			-- 수정날짜
			, stat_cd = #{statCd}
		where 
			bill_id = #{billId}

    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.updateRevokeCancle */
		update ebs_master 
		set 
			mod_id = #{modId} 			-- 수정자 아이디
			, mod_dt = now()			-- 수정날짜
			, stat_cd = #{statCd}
		where 
			bill_id = #{billId}
