mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.selectRevokeList */ select em.bill_id --안건아이디 , em.scl_dsc_rcp_nmb --사회토론번호 , case when #{lang} = 'langTy1' then em.bill_name_kg when #{lang} = 'langTy2' then em.bill_name_ru else em.bill_name_kg end as billName , cm.member_nm_kg --제안자 , to_char(em.reg_dt, 'DD/MM/YYYY') as regDate from kgst.ebs_master em left join kgst.com_member cm on cm.member_id = em.ppsr_id where 1=1 and em.ppsr_id = #{userId} and em.na_term_cd = '14'
and case when #{lang} = 'langTy1' then em.bill_name_kg when #{lang} = 'langTy2' then em.bill_name_ru else em.bill_name_kg end like concat('%', #{billName}, '%')
and em.stat_cd = #{statCd}
and em.reg_dt::date = #{ppslDt}
and em.scl_dsc_rcp_nmb like concat('%', #{sclDscRcpNmb}, '%')

mapper.update:
    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.updateRevokeRequset */
		update kgst.ebs_master 
		set 
			mod_id = #{modId} 			-- 수정자 아이디
			, mod_dt = now()			-- 수정날짜
			, stat_cd = #{statCd}
		where 
			bill_id = #{billId}

    	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revoke.repository.RevokeMapper.updateRevokeCancle */
		update kgst.ebs_master 
		set 
			mod_id = #{modId} 			-- 수정자 아이디
			, mod_dt = now()			-- 수정날짜
			, stat_cd = #{statCd}
		where 
			bill_id = #{billId}
