mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revokeAgree.repository.RevokeAgreeMapper.selectRevokeAgreeList */ select em.bill_id --안건아이디 , em.scl_dsc_rcp_nmb --사회토론번호 , case when #{lang} = 'lng_type_1' then em.bill_name_kg else em.bill_name_ru end as billName --안건이름 , case when #{lang} = 'lng_type_1' then view.member_nm_kg else view.member_nm_ru end as ppsrNm --제안자 , to_char(em.reg_dt, 'DD-MM-YYYY') as regDate --등록일 , case when (ep.wt_dt is not null and ep.wt_cnc_dt is null) then ep.sign_dt else null end as agreeYn --철회동의여부(날짜) from kgst.ebs_master em left join kgst.ebs_proposer ep on ep.bill_id = em.bill_id left join kgst.view_user_member view on view.member_id = em.ppsr_id where em.ppsr_id != #{userId} and ep.proposer_id = #{userId} and em.na_term_cd = '14'
and case when #{lang} = 'lng_type_1' then em.bill_name_kg else em.bill_name_ru end like concat('%', #{billName}, '%')
and em.stat_cd = #{statCd}
and em.reg_dt::date = #{ppslDt}

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.billApply.revokeAgree.repository.RevokeAgreeMapper.updateRevokeAgree */ update kgst.ebs_proposer set mod_id = #{userId} -- 수정자 아이디 , mod_dt = now() -- 수정날짜 -- 서명 취소 , wt_cnc_dt = now()
-- 서명 동의 , wt_dt = now() , wt_cnc_dt = null
where bill_id = #{billId} and proposer_id = #{userId}
