mapper.insert:
INSERT INTO EBS_MTNG
(
	  MTNG_TYPE_CD
	, dept_cd
	, MTNG_PLC
	, AGE_CD
	, RMK
	, STAT_CD
	, REG_ID
	, REG_DT
	, DUE_DTM
)
VALUES
(
	   #{mtngTypeCd}
	,  #{deptCd}
	,  #{mtngPlc}
	,  #{ageCd}
	,  #{rmk}
	,  #{statCd}
	,  #{regId}
	,  now()
	,  #{dueDtm}
)

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMember */ SELECT cm.member_id --국회의원아이디 , kgst.fngetlangnm(cm.member_nm_kg, cm.member_nm_ru, cm.member_nm_kg, #{lang}) as memberNm -- 제안자 이름 , cm.dept_id , cm.cmit_cd , cm.poly_cd , cm.poly_nm , 'ATT01' as atdt_kind FROM kgst.com_member cm WHERE 1=1
AND (cm.member_nm_kg LIKE CONCAT('%', #{searchNm}, '%') or cm.member_nm_ru LIKE CONCAT('%', #{searchNm}, '%'))
AND cm.cmit_cd = #{searchCmtId}
ORDER BY memberNm

/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMember_bak */ SELECT vum.member_id --국회의원아이디 , CASE WHEN #{lang} = 'lng_type_1' then vum.member_nm_kg WHEN #{lang} = 'lng_type_2' then vum.member_nm_ru ELSE vum.member_nm_kg END AS memberNm , vum.dept_cd , vum.cmit_cd , CASE WHEN #{lang} = 'lng_type_1' then vum.dept_nm_kg WHEN #{lang} = 'lng_type_2' then vum.dept_nm_ru ELSE vum.dept_nm_kg END AS deptNm , vum.poly_cd , vum.poly_nm FROM kgst.view_user_member vum WHERE 1=1
AND (vum.member_nm_kg LIKE CONCAT('%', #{searchNm}, '%') or vum.member_nm_ru LIKE CONCAT('%', #{searchNm}, '%'))
AND vum.cmit_cd = #{searchCmtId}
AND vum.dept_cd = #{searchDeptCd}
ORDER BY deptNm, memberNm

