		    )

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFrom */
    delete from ebs_mtng
    where mtng_id = #{mtngId}

	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromAttendant */
    delete from ebs_mtng_attendant
    where mtng_id = #{mtngId}

	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromAgenda */
    delete from ebs_mtng_agenda
    where mtng_id = #{mtngId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMtngBill */
select em.bill_id , em.bill_no , em.na_term_cd , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.user_nm_kg,vum.user_nm_ru,vum.user_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_name_kg , em.bill_name_ru , em.ppsr_id , vum.user_nm_kg , vum.user_nm_ru , em.ppsl_dt , emc.cmt_cd , emc.cmt_se_cd from kgst.ebs_master em join kgst.ebs_bp_instance ebi on (em.bill_id = ebi.bill_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) join kgst.ebs_master_cmt emc on (em.bill_id = emc.bill_id) where 1=1 and ebi.current_step_id not in ('0','1000','3200','3200','9999')
and emc.cmt_cd = #{deptCd}
order by em.reg_dt

/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMainMtngBill */
select em.bill_id , em.bill_no , em.na_term_cd , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.user_nm_kg,vum.user_nm_ru,vum.user_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_name_kg , em.bill_name_ru , em.ppsr_id , vum.user_nm_kg , vum.user_nm_ru , em.ppsl_dt , emc.cmt_cd , emc.cmt_se_cd from kgst.ebs_master em join kgst.ebs_bp_instance ebi on (em.bill_id = ebi.bill_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) join kgst.ebs_master_cmt emc on (em.bill_id = emc.bill_id) where 1=1 and ebi.current_step_id in ('1700','2300','2900')
and emc.cmt_cd = #{deptCd}
order by em.reg_dt

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateFromMtngBill */
update kgst.ebs_mtng
	set due_dtm = #{dueDtm}
	, mtng_plc = #{mtngPlc}
	, rmk = #{rmk}
	, mod_id = #{modId}
	, mod_dt = now()
where mtng_id = #{mtngId}::integer

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromBillAttendant */
    DELETE FROM kgst.ebs_mtng_attendant
    WHERE mtng_id = #{mtngId}

      AND atdt_user_id NOT IN

        #{attendant.atdtUserId}

mapper.update:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateMtngFromAttendant */
	INSERT INTO kgst.ebs_mtng_attendant
	     (
		       mtng_id
		     , atdt_user_id
		     , atdt_user_nm
		     , atdt_kind
		     , reg_id
		     , reg_dT
	     )
	VALUES
	     (
		       #{mtngId}
		     ,  #{atdtUserId}
		     ,  #{atdtUserNm}
		     ,  #{atdtKind}
		     ,  #{regId}
		     ,  now()
	    )
	ON CONFLICT (MTNG_ID, ATDT_USER_ID) DO UPDATE SET
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now()

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromBillAgenda */
    DELETE FROM kgst.ebs_mtng_agenda
    WHERE mtng_id = #{mtngId}

      AND bill_id NOT IN

	        #{agenda.billId}

mapper.insert:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateMtngFromAgenda */
	INSERT INTO kgst.ebs_mtng_agenda
	     (
		       mtng_id
		     , bill_id
		     , ord
		     , reg_id
		     , reg_dt
	     )
	VALUES
	     (
		       #{mtngId}
		     ,  #{billId}
		     ,  #{ord}
		     ,  #{regId}
		     ,  now()
	    )
	ON CONFLICT (MTNG_ID, BILL_ID) DO UPDATE SET
    ORD = EXCLUDED.ORD,
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now()
