mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMtngFrom */
SELECT em.mtng_id , em.mtng_type_cd , kgst.fngetcodenm('1014',em.mtng_type_cd,#{lang}) as mtng_type_nm , em.dept_cd , kgst.fngetlangnm(cd.dept_nm1,cd.dept_nm2,cd.dept_nm3,#{lang}) as dept_nm , em.excn_cycl , em.due_dtm , em.open_dtm , em.close_dtm , em.mtng_plc , em.age_cd , em.rmk , em.stat_cd , kgst.fngetcodenm('1007',em.stat_cd,#{lang}) as stat_nm , em.reg_id , em.reg_dt , em.session_no , em.session_ord FROM kgst.ebs_mtng em left outer join kgst.com_dept cd on (em.dept_cd = cd.dept_cd) WHERE 1=1
AND em.mtng_type_cd = #{mtngTypeCd}
AND em.stat_cd = #{statCd}
AND em.dept_cd = #{deptCd}
AND em.age_cd = #{searchAgeCd}
and em.due_dtm >= #{searchFromDate}
and em.due_dtm <= (#{searchToDate} || '9999')
order by em.due_dtm desc

mapper.insert:
INSERT INTO EBS_MTNG
(
	  MTNG_TYPE_CD
	, dept_cd
	, MTNG_PLC
	, AGE_CD
	, RMK
	, STAT_CD
	, REG_ID
	, REG_DT
	, DUE_DTM
	, OPEN_DTM
	, SESSION_NO
	, SESSION_ORD
)
VALUES
(
	   #{mtngTypeCd}
	,  #{deptCd}
	,  #{mtngPlc}
	,  #{ageCd}
	,  #{rmk}
	,  #{statCd}
	,  #{regId}
	,  now()
	,  #{dueDtm}
	,  #{openDtm}
	,  #{sessionNo}
	,  #{sessionOrd}
)

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMember */ select vum.member_id , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as member_nm , vum.age_cd , vum.dept_cd , kgst.fngetlangnm(vum.dept_nm_kg,vum.dept_nm_ru,vum.dept_nm_kg,#{lang}) as dept_nm , vum.cmit_cd , kgst.fngetlangnm(vum.cmit_nm_kg,vum.cmit_nm_ru,vum.cmit_nm_kg,#{lang}) as cmit_nm , vum.poly_cd , vum.poly_nm from view_user_member vum where vum.is_member = 'Y'
AND kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) LIKE CONCAT('%', #{memberNm}, '%')
AND vum.poly_cd = #{polyCd}
ORDER BY vum.poly_nm

