mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFrom */
    delete from ebs_mtng
    where mtng_id = #{mtngId}

	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromAttendant */
    delete from ebs_mtng_attendant
    where mtng_id = #{mtngId}

	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromAgenda */
    delete from ebs_mtng_agenda
    where mtng_id = #{mtngId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMtngBill */
select em.bill_id , em.bill_no , em.na_term_cd , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.user_nm_kg,vum.user_nm_ru,vum.user_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_name_kg , em.bill_name_ru , em.ppsr_id , vum.user_nm_kg , vum.user_nm_ru , em.ppsl_dt , emc.cmt_cd , emc.cmt_se_cd from kgst.ebs_master em join kgst.ebs_bp_instance ebi on (em.bill_id = ebi.bill_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) join kgst.ebs_master_cmt emc on (em.bill_id = emc.bill_id) where 1=1 and ebi.current_step_id not in ('0','1000','3200','3200','9999')
and emc.cmt_cd = #{deptCd}
and em.bill_no LIKE CONCAT('%', #{billNo}, '%')
AND kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) LIKE CONCAT('%', #{billNm}, '%')
order by em.reg_dt

/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListMainMtngBill */
select em.bill_id , em.bill_no , em.na_term_cd , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.user_nm_kg,vum.user_nm_ru,vum.user_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_name_kg , em.bill_name_ru , em.ppsr_id , vum.user_nm_kg , vum.user_nm_ru , em.ppsl_dt from kgst.ebs_master em join kgst.ebs_bp_instance ebi on (em.bill_id = ebi.bill_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) where 1=1 and ebi.current_step_id in ('1400','1500','1600')
and em.bill_no = #{billNo}
AND kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) LIKE CONCAT('%', #{billNm}, '%')
order by em.reg_dt

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateFromMtngBill */
update kgst.ebs_mtng
  set due_dtm = #{dueDtm}
	, mtng_plc = #{mtngPlc}
	, rmk = #{rmk}
	, mod_id = #{modId}
	, mod_dt = now()
	 , open_dtm = #{openDtm} 
	 , age_cd = #{ageCd} 
	 , session_no = #{sessionNo} 
	 , session_ord = #{sessionOrd} 
where mtng_id = #{mtngId}

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromBillAttendant */
    DELETE FROM kgst.ebs_mtng_attendant
    WHERE mtng_id = #{mtngId}

mapper.update:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateMtngFromAttendant */
	INSERT INTO kgst.ebs_mtng_attendant
	     (
		       mtng_id
		     , atdt_user_id
		     , atdt_user_nm
		     , atdt_kind
		     , reg_id
		     , reg_dT
	     )
	VALUES
	     (
		       #{mtngId}
		     ,  #{atdtUserId}
		     ,  #{atdtUserNm}
		     ,  #{atdtKind}
		     ,  #{regId}
		     ,  now()
	    )
	ON CONFLICT (MTNG_ID, ATDT_USER_ID) DO UPDATE SET
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now()

mapper.delete:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngFromBillAgenda */
    DELETE FROM kgst.ebs_mtng_agenda
    WHERE mtng_id = #{mtngId}

mapper.insert:
	/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateMtngFromAgenda */
	INSERT INTO kgst.ebs_mtng_agenda
	     (
		       mtng_id
		     , bill_id
		     , ord
		     , reg_id
		     , reg_dt
	     )
	VALUES
	     (
		       #{mtngId}
		     ,  #{billId}
		     ,  #{ord}
		     ,  #{regId}
		     ,  now()
	    )
	ON CONFLICT (MTNG_ID, BILL_ID) DO UPDATE SET
    ORD = EXCLUDED.ORD,
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now()

mapper.delete:
     /* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.deleteMtngAgenda */
	delete from kgst.ebs_mtng_agenda
	where mtng_id = #{mtngId}
	and bill_id = #{billId}

mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListHallMtngAgenda */ select ema.mtng_id , ema.bill_id , COALESCE(ema.rslt_cd, '') as rslt_cd , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.user_nm_kg,vum.user_nm_ru,vum.user_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_no , mt.open_dtm , mt.session_no , mt.session_ord from ebs_mtng mt join ebs_mtng_agenda ema on (mt.mtng_id = ema.mtng_id) join ebs_master em on (em.bill_id = ema.bill_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) where 1=1 and mt.mtng_type_cd = '2'
and mt.open_dtm LIKE CONCAT(#{searchDate}, '%')
and mt.session_no = #{searchSessionNo}
and mt.session_ord = #{searchSessionOrd}
and kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) LIKE CONCAT('%',#{billName}, '%')
and em.bill_no LIKE CONCAT('%',#{billNo}, '%')

/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.selectListHallMtng */
SELECT em.mtng_id , em.mtng_type_cd , kgst.fngetcodenm('1014',em.mtng_type_cd,#{lang}) as mtng_type_nm , em.dept_cd , kgst.fngetlangnm(cd.dept_nm1,cd.dept_nm2,cd.dept_nm3,#{lang}) as dept_nm , em.excn_cycl , em.due_dtm , em.open_dtm , em.close_dtm , em.mtng_plc , em.age_cd , em.rmk , em.stat_cd , kgst.fngetcodenm('1007',em.stat_cd,#{lang}) as stat_nm , em.reg_id , em.reg_dt , em.session_no , em.session_ord FROM kgst.ebs_mtng em left outer join kgst.com_dept cd on (em.dept_cd = cd.dept_cd) WHERE 1=1 AND em.mtng_type_cd = '2'
and em.open_dtm LIKE CONCAT(#{searchDate}, '%')
and em.session_no = #{searchSessionNo}
and em.session_ord = #{searchSessionOrd}
order by em.open_dtm desc

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngFrom.repository.MtngFromMapper.updateHallMtngResult */
update kgst.ebs_mtng_agenda
  set mod_id = #{modId}
	, mod_dt = now()
	, rslt_cd = #{rsltCd}
where mtng_id = #{mtngId}
  and bill_id = #{billId}
