mapper.select:
/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.mtng.mtngTo.repository.MtngToMapper.selectListMember */ SELECT cm.member_id --국회의원아이디 , cm.member_nm_kg --이름(키르키즈어) , cm.member_nm_ru --이름(러시아어) , cm.age_cd --대수 , cm.cmit_cd --소속위원회코드 , CASE WHEN #{lang} = 'lng_type_1' then cd.dept_nm1 WHEN #{lang} = 'lng_type_2' then cd.dept_nm2 ELSE cd.dept_nm3 END AS cmitNm , cm.dept_id --부서 , cm.poly_cd --정당코드 , cm.poly_nm --정당명 , cm.rsdn_rgst_nmbr --주민번호 본인을 구분하는 번호 , cm.email -- 이메일주소 , cm.gen_cd --성별(M,F) , cm.profile_img_path --프로필이미지경로 , cm.mbl_no --이동전화번호 , cm.msg_rcv_yn --메세지수신여부 , cm.doc_mgr_yn --문서관리자여부 , cm.reg_id , cm.reg_dt , cm.mod_id , cm.mod_dt FROM com_member cm , com_dept cd , ebs_base_code ebc WHERE 1=1 AND cm.cmit_cd = cd.dept_cd AND (cm.age_cd = ebc.code_id AND ebc.curr_yn = 'Y')
AND cm.member_nm_kg LIKE CONCAT('%', #{memberNmKg}, '%')
ORDER BY cm.poly_nm, cm.member_nm_kg

mapper.insert:
INSERT INTO EBS_MTNG_ATTENDANT
     (
       MTNG_ID
     , ATDT_USER_ID
     , ATDT_USER_NM
     , ATDT_KIND
     , REG_ID
     , REG_DT
     )
VALUES
     (
       #{mtngId}
     ,  #{atdtUserId}
     ,  #{atdtUserNm}
     ,  #{atdtKind}
     ,  #{regId}
     ,  now()
    )

INSERT INTO EBS_MTNG_AGENDA
     (
       MTNG_ID
     , BILL_ID
     , ORD
     , REG_ID
     , REG_DT
     )
VALUES
     (
       #{mtngId}
     ,  #{billId}
     ,  #{ord}
     ,  #{regId}
     ,  now()
    )

mapper.update:
UPDATE EBS_MTNG
     SET OPEN_DTM = #{openDtm}
     , CLOSE_DTM = #{closeDtm}
     , MTNG_PLC = #{mtngPlc}
     , RMK = #{rmk}
     , MOD_ID = #{modId}
     , MOD_DT = now()
 WHERE MTNG_ID = #{mtngId}

UPDATE EBS_MTNG
SET
       MOD_ID = #{modId}
     , MOD_DT = now()
     , stat_cd = #{statCd}
 WHERE MTNG_ID = #{mtngId}

/* QueryID : kr.co.bestiansoft.ebillservicekg.common.file.repository.FileMapper.updateFileEbs */
update kgst.ebs_mtng_file
SET mod_dt = now()
  , mod_id = #{modId}
  , delete_yn = 'Y'
where
	mtng_id = #{mtngId};

mapper.delete:
    DELETE FROM ebs_mtng_agenda
    WHERE mtng_id = #{mtngId}

      AND bill_id NOT IN

	        #{agenda.billId}

mapper.insert:
	INSERT INTO EBS_MTNG_AGENDA
	     (
	       MTNG_ID
	     , BILL_ID
	     , ORD
	     , RSLT_CD
	     , REG_ID
	     , REG_DT
	     )
	VALUES
	     (
	       #{mtngId}
	     ,  #{billId}
	     ,  #{ord}
	     ,  #{rsltCd}
	     ,  #{regId}
	     ,  now()
	    )
	ON CONFLICT (MTNG_ID, BILL_ID) DO UPDATE SET
    ORD = EXCLUDED.ORD,
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now(),
    RSLT_CD = EXCLUDED.RSLT_CD

mapper.delete:
DELETE FROM ebs_mtng_attendant
WHERE mtng_id = #{mtngId}

  AND atdt_user_id NOT IN

    #{attendant.atdtUserId}

mapper.update:
	INSERT INTO EBS_MTNG_ATTENDANT
	     (
	       MTNG_ID
	     , ATDT_USER_ID
	     , ATDT_USER_NM
	     , ATDT_KIND
	     , ATDT_DIV_CD
	     , REG_ID
	     , REG_DT
	     )
	VALUES
	     (
	       #{mtngId}
	     ,  #{atdtUserId}
	     ,  #{atdtUserNm}
	     ,  #{atdtKind}
	     ,  #{atdtDivCd}
	     ,  #{regId}
	     ,  now()
	    )
	ON CONFLICT (MTNG_ID, ATDT_USER_ID) DO UPDATE SET
    MOD_ID = EXCLUDED.REG_ID, -- 삽입 값 참조
    MOD_DT = now(),
    ATDT_DIV_CD = EXCLUDED.ATDT_DIV_CD
