/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.review.billMng.repository.BillMngMapper.selectListBillMng */ select * from ( select pl.bill_id , kgst.fngetlangnm(pl.bill_name_kg,pl.bill_name_ru,pl.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , kgst.fngetlangnm(pl.etc_kg,pl.etc_ru,pl.etc_kg,#{lang}) as etc -- etc , pl.bill_name_kg , pl.bill_name_ru , pl.bill_no , pl.scl_dsc_rcp_nmb , pl.bill_kind , pl.na_term_cd , pl.ppsr_id , pl.rcp_dt , vum.member_nm_kg , vum.member_nm_ru , pl.ppsl_dt , pl.current_step_id , pl.task_id , pl.task_status , pl.step_id , pl.stat_cd , kgst.fngetcodenm('1010',pl.stat_cd,#{lang}) as stat_nm , kgst.fngetcodenm('1034',pl.task_status,#{lang}) as task_stat_nm , pl.assigned_to , pl.trgt_user_id , pl.reg_dt , pl.task_reg_dt , TO_CHAR(pl.completed_dt, 'DD/MM/YYYY') as completed_dt from kgst.view_bill_process_list pl join kgst.view_user_member vum on (pl.ppsr_id = vum.user_id) where 1=1
and pl.assigned_to = #{assignedTo}
and pl.trgtUserId = #{trgtUserId}
and pl.step_id = #{stepId}
and pl.task_status = #{taskStatus}
and pl.na_term_cd = #{naTermCd}
) x where 1=1
and x.bill_name like concat('%', #{billName}, '%')
and x.bill_no like concat('%', #{billNo}, '%')
and x.ppsl_dt >= #{ppslDtFrom}
and x.ppsl_dt <= #{ppslDtTo}
and x.stat_cd = #{statCd}
order by x.task_status desc, x.rcp_dt desc

