/* QueryID : kr.co.bestiansoft.ebillservicekg.bill.review.billMng.repository.BillMngMapper.selectListCmtReviewReport */
select A.bill_id , max(A.bill_no) as bill_no , max(A.bill_name) as bill_name , max(A.ppsr_nm) as ppsr_nm , max(A.ppsr_id) as ppsr_id , max(A.ppsl_dt) as ppsl_dt , max(A.na_term_cd) as na_term_cd , max(A.cmt_cd) as cmt_cd , max(A.cmt_se_cd) as cmt_se_cd , max(A.cmt_se_nm) as cmt_se_nm , max(A.mtng_id) as mtng_id , max(A.rslt_cd) as rslt_cd , max(A.mtng_type_cd) as mtng_type_cd , max(A.age_cd) as age_cd , max(A.stat_nm) as stat_nm , max(A.stat_cd) as stat_cd from ( select em.bill_id , em.bill_no , kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) as bill_name -- 안건 이름 , kgst.fngetlangnm(vum.member_nm_kg,vum.member_nm_ru,vum.member_nm_kg,#{lang}) as ppsr_nm -- 제안자 이름 , em.bill_name_kg , em.bill_name_ru , em.ppsr_id , em.ppsl_dt , em.na_term_cd , emc.cmt_cd , emc.cmt_se_cd , kgst.fngetcodenm('1018',emc.cmt_se_cd,#{lang}) as cmt_se_nm , ema.mtng_id , ema.rslt_cd , emt.mtng_type_cd , emt.age_cd , step.stat_cd , kgst.fngetcodenm('1010',step.stat_cd,#{lang}) as stat_nm from kgst.ebs_master em join kgst.ebs_master_cmt emc on (em.bill_id = emc.bill_id) join kgst.ebs_mtng_agenda ema on (em.bill_id = ema.bill_id) join kgst.ebs_mtng emt on (ema.mtng_id = emt.mtng_id) join kgst.view_user_member vum on (em.ppsr_id = vum.user_id) join kgst.ebs_bp_instance ebi on (em.bill_id = ebi.bill_id) join kgst.ebs_bp_step step on (ebi.bp_df_id = step.bp_df_id and ebi.current_step_id = step.step_id) where 1=1 and em.na_term_cd = #{naTermCd} and emc.cmt_cd = #{cmtCd}
and emc.cmt_se_cd = #{cmtSeCd}
and step.stat_cd = #{statCd}
and em.bill_no like concat('%', #{billNo}, '%')
and kgst.fngetlangnm(em.bill_name_kg,em.bill_name_ru,em.bill_name_kg,#{lang}) like concat('%', #{billName}, '%')
and em.ppsl_dt >= #{ppslDtFrom}
and em.ppsl_dt <= #{ppslDtTo}
) A where 1=1
group by A.bill_id order by ppsl_dt

