	, org_file_id
	, org_file_nm
	, pdf_file_id
	, pdf_file_nm
	, rmk
	, opb_yn
	, delete_yn
	, file_size
	, reg_id
	, reg_dt
)
values (
	  #{billId}
	, #{fileKindCd}
	, #{orgFileId}
	, #{orgFileNm}
	, #{pdfFileId}
	, #{pdfFileNm}
	, #{rmk}
	, #{opbYn}
	, 'N'
	, #{fileSize}
	, #{regId}
	, now()
)

mapper.update:
/* QueryID : kr.co.bestiansoft.ebillservicekg.common.file.repository.FileMapper.updateFileEbs */
update kgst.ebs_file
SET mod_dt = now()
  , mod_id = #{modId}
   , bill_id = #{billId} 
   , file_kind_cd = #{fileKindCd} 
   , org_file_nm = #{orgFileNm} 
   , pdf_file_id = #{pdfFileId} 
   , pdf_file_nm = #{pdfFileNm} 
   , rmk = #{rmk} 
   , opb_yn = #{opbYn} 
   , delete_yn = #{deleteYn} 
   , file_size = #{fileSize} 
where
	org_file_id = #{orgFileId};
