mapper.insert:
INSERT INTO kgst.eas_approval
(doc_id, user_id, user_nm, dept_cd, job_cd, apvl_ord, res_dtm, rcv_dtm, check_dtm, apvl_file_id, res_opinion, apvl_status_cd, apvl_type)
VALUES(#{docId}, #{userId}, #{userNm}, #{deptCd}, #{jobCd}, #{apvlOrd}, #{resDtm}, #{rcvDtm}, #{checkDtm}, #{apvlFileId}, #{resOpinion}, #{apvlStatusCd}, #{apvlType});

mapper.update:
update kgst.eas_approval
   set apvl_status_cd = #{apvlStatusCd}
 where apvl_id = #{apvlId}

update kgst.eas_approval
res_dtm = #{resDtm}, rcv_dtm = #{rcvDtm}, check_dtm = #{checkDtm}, res_opinion = #{resOpinion}, apvl_file_id = #{apvlFileId}, apvl_status_cd = #{apvlStatusCd},
where apvl_id = #{apvlId}

mapper.select:
select apvl_id, doc_id, user_id, user_nm, dept_cd, job_cd, apvl_ord, res_dtm, rcv_dtm, check_dtm, apvl_file_id, res_opinion, apvl_status_cd, apvl_type
  from eas_approval
 where apvl_id =#{apvlId}

select apvl_id, doc_id, user_id, user_nm, dept_cd, job_cd, apvl_ord, res_dtm, rcv_dtm, check_dtm, apvl_file_id, res_opinion, apvl_status_cd, apvl_type
  from kgst.eas_approval
 where doc_id =#{docId}
   and user_id = #{userId}
 order by apvl_ord
