mapper.insert:
INSERT INTO kgst.eas_history
    (doc_id, user_id, user_nm, act_type, act_detail, act_dtm)
VALUES(#{docId}, #{userId}, #{userNm}, #{actType}, #{actDetail}, #{actDtm});

mapper.select:
select history_id, doc_id, user_id, user_nm, act_type, act_detail , act_dtm
  from eas_history
 where doc_id = #{docId}

select history_id, doc_id, user_id, user_nm, act_type, act_detail , act_dtm
from eas_history
where doc_id = #{docId}
  and user_id = #{userId}
