mapper.insert:
INSERT INTO kgst.eas_document
(doc_id, aars_doc_id, user_id, doc_type_cd, doc_attrb_cd, bill_id, tmlmt_yn, tmlmt_dtm, doc_lng, doc_subtle, doc_status_cd, digital_yn, sender_id, sender_nm, dept_cd, doc_no, reg_dtm)
VALUES(#{docId}, #{aarsDocId}, #{userId}, #{docTypeCd}, #{docAttrbCd}, #{billId}, #{tmlmtYn}, #{tmlmtDtm}, #{docLng}, #{docSubtle}, #{docStatusCd}, #{digitalYn}, #{senderId}, #{senderNm}, #{deptCd}, #{docNo}, #{regDtm});

mapper.update:
update kgst.eas_document
   set doc_status_cd = #{docStatusCd}
 where doc_id = #{docId}

mapper.select:
select d.doc_id, doc_attrb_cd, d.tmlmt_yn, d.tmlmt_Dtm, doc_subtle, sender_id, sender_nm, doc_no, rcv_dtm, check_dtm, rcv_id, doc_type_cd
  from eas_document d, eas_received_info r
 where d.doc_id = r.doc_id
   and r.user_id = #{userId}
   and doc_status_cd in ('DMS02', 'DMS03')
   and rcv_status in ('RS01', 'RS02', 'RS03')
   and doc_subtle like '%'||#{docSubtle}||'%' 
   and tmlmt_dtm = #{tmlmtDtm} 
   and rcv_dtm between #{fromRcvDtm} and #{toRcvDtm} 
   and doc_no like '%'||#{docNo}||'%' 
   and doc_type_cd = #{docTypeCd}; 
   and sender_nm like '%'||#{senderNm}||'%' 
order by rcv_dtm desc

select doc_id, doc_attrb_cd, doc_subtle, sender_id, sender_nm, doc_no
  from eas_document d
 where d.user_id= #{userId}
   and d.doc_status_cd ='DMS04'
 order by reg_dtm desc;

select doc_id, doc_attrb_cd, doc_subtle, sender_id, sender_nm, doc_no
  from eas_document d
 where d.user_id= #{userId}
 order by reg_dtm desc;
