    , img_se
    , file_path
    , file_nm
    , orgnl_file_nm
    , file_extn
    , file_sz
    , reg_dt
    , rgtr_id
) values (
      #{imgId}
    , #{eqpmntId}
    , #{instlId}
    , #{imgSe}
    , #{filePath}
    , #{fileNm}
    , #{orgnlFileNm}
    , #{fileExtn}
    , #{fileSz}
    , now()
    , #{rgtrId}
