mapper.insert:
insert into ams_eqpmnt (
      eqpmnt_id
    , eqpmnt_cd
    , eqpmnt_se
    , eqpmnt_nm
    , expln
    , bzenty_id
    , tkcg_dept_id
    , reg_dt
    , rgtr_id
) values (
      #{eqpmntId}
    , #{eqpmntCd}
    , #{eqpmntSe}
    , #{eqpmntNm}
    , #{expln}
    , #{bzentyId}
    , #{tkcgDeptId}
    , now()
    , #{rgtrId}
)

mapper.update:
update ams_eqpmnt
set
      eqpmnt_cd = #{eqpmntCd}
    , eqpmnt_se = #{eqpmntSe}
    , eqpmnt_nm = #{eqpmntNm}
    , expln = #{expln}
    , bzenty_id = #{bzentyId}
    , tkcg_dept_id = #{tkcgDeptId}
    , mdfcn_dt = now()
    , mdfr_id = #{mdfrId}
where eqpmnt_id = #{eqpmntId}
