mapper.select:
select mnl_id , eqpmnt_id , mnl_se , seq , file_path , file_nm , orgnl_file_nm , file_extn , file_sz , mnl_lng from ams_mnl where 1=1
and eqpmnt_id in
#{id}

select mnl_id , eqpmnt_id , mnl_se , seq , file_path , file_nm , orgnl_file_nm , file_extn , file_sz , mnl_lng from ams_mnl where eqpmnt_id = #{eqpmntId}
and mnl_se = 'video'
order by seq

mapper.insert:
insert into ams_mnl (
      mnl_id
    , eqpmnt_id
    , mnl_se
    , seq
    , file_path
    , file_nm
    , orgnl_file_nm
    , file_extn
    , file_sz
    , mnl_lng
    , reg_dt
    , rgtr_id
)

    (
      #{vo.mnlId}
    , #{vo.eqpmntId}
    , #{vo.mnlSe}
    , #{vo.seq}
    , #{vo.filePath}
    , #{vo.fileNm}
    , #{vo.orgnlFileNm}
    , #{vo.fileExtn}
    , #{vo.fileSz}
    , #{vo.mnlLng}
    , now()
    , #{vo.rgtrId}
    )

mapper.update:
insert into ams_mnl (
      mnl_id
    , eqpmnt_id
    , mnl_se
    , seq
    , file_path
    , file_nm
    , orgnl_file_nm
    , file_extn
    , file_sz
    , mnl_lng
    , reg_dt
    ) values (
      #{mnlId}
    , #{eqpmntId}
    , #{mnlSe}
    , #{seq}
    , #{filePath}
    , #{fileNm}
    , #{orgnlFileNm}
    , #{fileExtn}
    , #{fileSz}
    , #{mnlLng}
    , now()
    , #{rgtrId}
    )
on conflict (mnl_id)
do update set
      mnl_se = excluded.mnl_se
    , seq = excluded.seq
    , file_path = excluded.file_path
    , file_nm = excluded.file_nm
    , orgnl_file_nm = excluded.orgnl_file_nm
    , file_extn = excluded.file_extn
    , file_sz = excluded.file_sz
    , mnl_lng = excluded.mnl_lng
    , mdfcn_dt = now()
    , mdfr_id = #{mdfrId}
where ams_mnl.mnl_id = excluded.mnl_id
