    , file_nm
    , orgnl_file_nm
    , file_extn
    , file_sz
    , mnl_lng
    , reg_dt
    , rgtr_id
)

    (
      #{vo.mnlId}
    , #{vo.eqpmntId}
    , #{vo.mnlSe}
    , #{vo.seq}
    , #{vo.filePath}
    , #{vo.fileNm}
    , #{vo.orgnlFileNm}
    , #{vo.fileExtn}
